if (element.value() != null && element.value().length == 5){
  var client = new XMLHttpRequest();
  var zip = element.value();
  var protocol = window.location.protocol;
  var url = protocol + "//api.zippopotam.us/us/" + zip
  client.open("GET", url, true);
  client.onreadystatechange = function() {
    if(client.readyState == 4) {    
      x = JSON.parse(client.responseText);
      K('field[City]').value(x.places[0]['place name'])
      K('field[State]').value(x.places[0]['state abbreviation'])
    };
  };
  client.send();
}

K('field[Resources Required]').value([]);
